<script>
import Button from '~/components/elements/Button'
import {features} from "~/services/api/home";

export default {
  components: {Button},
  data: () => ({
    boxes: []
  }),
  mounted() {
    features((isOkay, data) => {
      if (isOkay) {
        this.boxes.push(data)
      } else {
        console.log(data)
      }
    })
  }
}
</script>

